#include <iostream>
#include <string>
#include <vector>
#include <iomanip>
#include <map>
#include <algorithm>
#include <sstream>
#include <optional>

using namespace std;

// Класс для хранения информации о химическом элементе
class AtomicElement {
    // создал zeroqxq
private:
    int atomicNumber;
    string symbol;
    string name;
    string englishName;
    double atomicMass;
    double density; // g/cm³
    optional<double> meltingPoint; // K
    optional<double> boilingPoint; // K
    string atomicRadiusType;
    double atomicRadius; // pm
    string electronConfiguration;
    vector<string> oxidationStates;
    string electronegativity;
    string ionizationEnergy; // eV
    string crystalStructure;
    string groupBlock;
    int period;
    int group;
    string discoveryYear;
    string discoverers;

public:
    // Конструктор
    AtomicElement(int number, string sym, string nm, string engNm,
        double mass, double dens, 
        optional<double> melt, 
        optional<double> boil,
        string radiusType, 
        double radius, 
        string config,
        vector<string> oxStates, 
        string elneg,
        string ionEnergy, 
        string crystal, 
        string block,
        int per, int grp, string discYear, string disc)
        : atomicNumber(number), symbol(sym), name(nm), englishName(engNm),
        atomicMass(mass), density(dens), meltingPoint(melt), boilingPoint(boil),
        atomicRadiusType(radiusType), atomicRadius(radius),
        electronConfiguration(config), oxidationStates(oxStates),
        electronegativity(elneg), ionizationEnergy(ionEnergy),
        crystalStructure(crystal), groupBlock(block), period(per), group(grp),
        discoveryYear(discYear), discoverers(disc) {
    }

    // Метод для вывода полной информации об элементе
    void printFullInfo() const {
        cout << "\n=================================\n";
        cout << "| " << left << setw(30) << ("Элемент: " + name + " (" + englishName + ")") << "|\n";
        cout << "|===================================\n";
        cout << "| " << setw(15) << "Символ:" << setw(15) << symbol << "|\n";
        cout << "| " << setw(15) << "Атомный номер:" << setw(15) << atomicNumber << "|\n";
        cout << "| " << setw(15) << "Атомная масса:" << setw(15) << atomicMass << " u|\n";
        cout << "| " << setw(15) << "Плотность:" << setw(15) << density << " g/cm³|\n";
        cout << "| " << setw(15) << "Темп. плавл.:" << setw(15)
            << (meltingPoint.has_value() ? to_string(*meltingPoint) : "нет данных")
            << " K|\n";
        cout << "| " << setw(15) << "Темп. кипения:" << setw(15)
            << (boilingPoint.has_value() ? to_string(*boilingPoint) : "нет данных")
            << " K|\n";
        cout << "| " << setw(15) << "Атомный радиус:" << setw(15) << atomicRadius << " pm|\n";
        cout << "| " << setw(15) << "Тип радиуса:" << setw(15) << atomicRadiusType << "|\n";
        cout << "|===================================|\n";
        cout << "| " << setw(30) << ("Электронная конфигурация: " + electronConfiguration) << "|\n";

        ostringstream oss;
        for (const auto& state : oxidationStates) {
            oss << state << " ";
        }
        cout << "| " << setw(30) << ("Степени окисления: " + oss.str()) << "|\n";

        cout << "| " << setw(30) << ("Электроотрицательность: " + electronegativity) << "|\n";
        cout << "| " << setw(30) << ("Энергия ионизации: " + ionizationEnergy + " eV") << "|\n";
        cout << "| " << setw(30) << ("Кристалл. структура: " + crystalStructure) << "|\n";
        cout << "| " << setw(30) << ("Группа: " + groupBlock) << "|\n";
        cout << "| " << setw(30) << ("Период/Группа: " + to_string(period) + "/" + to_string(group)) << "|\n";
        cout << "|================================================|\n";
        cout << "| " << setw(30) << ("Открыт в: " + discoveryYear) << "|\n";
        cout << "| " << setw(30) << ("Первооткрыватели: " + discoverers) << "|\n";
        cout << "|================================================|\n";
    }

    // Метод для вывода краткой информации об элементе
    void printBasicInfo() const {
        cout << setw(3) << atomicNumber << " | "
            << setw(2) << symbol << " | "
            << setw(15) << name << " | "
            << setw(6) << atomicMass << " u | "
            << groupBlock << "\n";
    }

    // Геттеры
    int getAtomicNumber() const { return atomicNumber; }
    string getSymbol() const { return symbol; }
    string getName() const { return name; }
    string getGroupBlock() const { return groupBlock; }
    double getAtomicMass() const { return atomicMass; }
};
