    void displayMainMenu() {
        cout << "\n=== Периодическая система элементов ===" << endl;
        cout << "1. Поиск элемента по атомному номеру/символу" << endl;
        cout << "2. Показать все элементы (краткий список)" << endl;
        cout << "3. Показать элементы по группам" << endl;
        cout << "4. Поиск по диапазону атомных масс" << endl;
        cout << "5. Выход" << endl;
        cout << "6. Очистка экрана" << endl;
        cout << "Выберите действие: ";
    }

    void printElementInfo(const AtomicElement & element) {
        element.printFullInfo();
    }

    void searchElement(const map<int, AtomicElement>&elementsByNumber,
        const map<string, AtomicElement>&elementsBySymbol) {
        string input;
        cout << "Введите атомный номер или символ элемента: ";
        cin >> input;

        try {
            int num = stoi(input);
            auto it = elementsByNumber.find(num);
            if (it != elementsByNumber.end()) {
                printElementInfo(it->second);
            }
            else {
                cout << "Элемент с номером " << num << " не найден!" << endl;
            }
        }
        catch (const invalid_argument&) {
            string symbol = input;
            transform(symbol.begin(), symbol.end(), symbol.begin(), ::toupper);
            auto it = elementsBySymbol.find(symbol);
            if (it != elementsBySymbol.end()) {
                printElementInfo(it->second);
            }
            else {
                cout << "Элемент с символом " << symbol << " не найден!" << endl;
            }
        }
    }

    void showAllElementsBrief(const map<int, AtomicElement>&elements) {
        cout << "\nСписок всех элементов:" << endl;
        cout << "==================================================================" << endl;
        cout << left << setw(4) << "№" << "  " << "Sym" << "             " << "Name"
            << "       " << "Mass" << "        " << "Group" << endl;
        cout << "==================================================================" << endl;

        for (const auto& pair : elements) {
            pair.second.printBasicInfo();
        }
    }

    void showElementsByGroup(const map<int, AtomicElement>&elements) {
        map<string, vector<AtomicElement>> groupedElements;

        for (const auto& pair : elements) {
            groupedElements[pair.second.getGroupBlock()].push_back(pair.second);
        }

        cout << "\nЭлементы по группам:" << endl;
        for (const auto& group : groupedElements) {
            cout << "\n=== " << group.first << " ===" << endl;
            for (const auto& elem : group.second) {
                cout << setw(3) << elem.getAtomicNumber() << " "
                    << setw(2) << elem.getSymbol() << " "
                    << setw(15) << elem.getName() << endl;
                // создал zeroqxq
            }
        }
    }

    void searchByMassRange(const map<int, AtomicElement>&elements) {
        double minMass, maxMass;
        cout << "Введите минимальную атомную массу: ";
        cin >> minMass;
        cout << "Введите максимальную атомную массу: ";
        cin >> maxMass;
        // создал zeroqxq

        cout << "\nЭлементы в диапазоне масс " << minMass << " - " << maxMass << " u:" << endl;
        bool found = false;

        for (const auto& pair : elements) {
            if (pair.second.getAtomicMass() >= minMass &&
                pair.second.getAtomicMass() <= maxMass) {
                cout << setw(3) << pair.first << " "
                    << setw(2) << pair.second.getSymbol() << " "
                    << setw(15) << pair.second.getName()
                    << " - " << pair.second.getAtomicMass() << " u" << endl;
                found = true;
            }
        }

        if (!found) {
            cout << "Элементы в указанном диапазоне не найдены." << endl;
        }
    }

    int main() {
        setlocale(LC_ALL, "Ru");
        map<int, AtomicElement> elementsByNumber;
        map<string, AtomicElement> elementsBySymbol;
        system("chcp 65001>nul");
        initializeCompleteElementDatabase(elementsByNumber, elementsBySymbol);

        int choice;
        while(true) {
            displayMainMenu();
            cin >> choice;
            cin.ignore(); // Очистка буфера ввода

            switch (choice) {
            case 1:
                searchElement(elementsByNumber, elementsBySymbol);
                break;
            case 2:
                showAllElementsBrief(elementsByNumber);
                break;
            case 3:
                showElementsByGroup(elementsByNumber);
                break;
            case 4:
                searchByMassRange(elementsByNumber);
                break;
            case 5:
                cout << "Выход из программы..." << endl;
                break;
            case 6:
                system("cls");
            default:
                cout << "Неверный выбор! Попробуйте снова." << endl;
            }
        }

        return 0;
    }